INSERT INTO studentcommunity (name, abbrevation, title, description, color) VALUES ('Prosjektutvalg for spill', 'PUS', 'Spill', 'For deg som elsker spill! Her møtes man på skolen for å spille. Man er også med på å arrangere lan partys, og være ansvarlig for konkurranser.', 'f7bc78');
INSERT INTO studentcommunity (name, abbrevation, title, description, color) VALUES ('Prosjektutvalg for support', 'PUSU', 'Support', 'Skolens elite! Dette er gruppen for deg som elsker å hjelpe andre i nød. En meget sosial gruppe som får i oppgave om å løse diverse saker!', '004dcc');
INSERT INTO studentcommunity (name, abbrevation, title, description, color) VALUES ('Prosjektutvalg for ledere', 'PUL', 'Ledere', 'Alle utvalg trenger ledere, dette er gruppen for deg dersom du ønsker å være leder i noen av skolens utvalg. Man trenger ingen erfaring fra tidligere. Her får den hjelpen du trenge for å bli en leder.', '000000');
INSERT INTO studentcommunity (name, abbrevation, title, description, color) VALUES ('Prosjektutvalg for kvinner', 'PUK', 'Kvinner', 'Gruppen for alle skolens kvinner, er du ikke medlem i dag meld deg inn nå! Det er ikke til å legge skjul på at kvinner er i mindre tall når det gjelder IT. Dette utvalget arrangerer hyggelige kvelder, det hender bedrifter stikker innom for å smiske.', 'f79fe7');
INSERT INTO studentcommunity (name, abbrevation, title, description, color) VALUES ('Prosjektutvalg for sosial', 'PUSO', 'Sosial', 'For de på skolen som brenner for å arrangere. Her får du en unik mulighet til å påvirke studentene til å være med på alt fra festlige hytte turer til konserter av alle slag. Julebordet på skolen er alltid en suksess!', '9fdff7');
INSERT INTO studentcommunity (name, abbrevation, title, description, color) VALUES ('Prosjektutvalg for kroa', 'PUKR', 'Kroa', 'Elsker du å henge på skolen er du hjertelig velkommen i kro utvalget, en ekstrem sosial gjeng som har det veldig moro! Stillingen består Hovedsakelig og selge øl og lignende. Arbeidet er ikke lønnet men man får meget gode rabatter. Kroa samarbeider tett med sosial-utvalget for å arrangere fester i kroa.', 'a26a2b');
INSERT INTO studentcommunity (name, abbrevation, title, description, color) VALUES ('Prosjektutvalg for musikk', 'PUM', 'Musikk', 'Musikkutvalget er for deg med en lidenskap for musikk! Dette utlvalget vil komme med informasjon om konserter rundt om kring i Oslo, og arrangere kvelder hvor man kan dra på disse som en gruppe. Det blir også arrangert intimkonserter med kjente og ukjente navn. I tillegg til dette blir det arrangert workshop hvor de mer erfarne musikerne av oss kan lære de som har lyst forskjellige instrumenter.', '93d888');
INSERT INTO studentcommunity (name, abbrevation, title, description, color) VALUES ('Prosjektutvalg for film', 'PUF', 'Film', 'Er du interessert i film? Da er dette for deg! Her får du muligheten til å diskutere film med mennesker som har samme lidenskap som deg. Det blir også arrangert filmkvelder en gang i uka for de som er interesserte, og kinokvelder en gang måneden. Det er mange erfarne filmstudenter i utvalget, og disse vil stille opp på kvelder for å lære de som er interesserte i hvordan det er å jobbe med film.', 'ff0000');
INSERT INTO studentcommunity (name, abbrevation, title, description, color) VALUES ('Prosjektutvalg for kunst', 'PUKU', 'Kunst', 'Bor det en kunstner i deg? Da må du melde deg på kunstutvalget! Det blir arrangert kvelden hvor vi samles og drar på museum, galleri og utsillinger. Det blir også holdt male-, skulptur-, og fotokurs for de av dere som er interesserte i det.', 'ffe400');
INSERT INTO studentcommunity (name, abbrevation, title, description, color) VALUES ('Prosjektutvalg for teater', 'PUT', 'Teater', 'Brenner du for teater? Da er teaterutvalget for deg! Her arrangeres det kvelder hvor vi ser teater sammer, og diskuterer stykket i etterkant. I løpet av året skal vi også sette opp vår egen produksjon som i slutten av året skal vises fram til hele skolen. Alle er velkommen, og alle vil få en rolle å spille!', 'a06dcb');
INSERT INTO studentcommunity (name, abbrevation, title, description, color) VALUES ('Prosjektutvalg for apple', 'PUA', 'Apple', 'Dette er utvalget for oss som elsker alt Apple! Her diskuterer vi nye og kommende produkter, og anmelder disse i vårt nyhetsmagasin; Apple Times. Om du ønsker å skrive for Apple Times har du selvfølgelig også mulighet til dette. Meld deg på!', 'c9c9c9');
INSERT INTO studentcommunity (name, abbrevation, title, description, color) VALUES ('Prosjektutvalg for idrett', 'PUI', 'Idrett', 'Dette er utvalget for deg som er sporty! I idrettsutvalget blir det gjevnlig holdt treningsøkter i både fotball, basketball, håndball m.m. Det blir også i løpet av vinteren arrangert skiturer og skihopp for de som ønsker dette. Er du med i idrettsutvalget kan du også melde deg opp til de forskjellige lagene vi har. Kampene spilles mot andre studentforeninger og bedriftslag. Du må ikke være proff for å være med her, så bare meld deg på!', '2d770d');
INSERT INTO studentcommunity (name, abbrevation, title, description, color) VALUES ('Prosjektutvalg for skriving', 'PUSK', 'Skriving', 'Skriveutvalget er for deg som liker å skrive. Det er spiller ingen rolle om du skriver noveller eller blogger, her er det plass til alle! Vi vil arrangere skriveworkshops hvor man får tips til hvordan man forbedre egen skriving, og det blir invitert forfattere til å holde foredrag og kurs.', '000000');
INSERT INTO studentcommunity (name, abbrevation, title, description, color) VALUES ('Prosjektutvalg for utvikling', 'PUU', 'Utvvikling', 'Utviklingsutvalget er for deg som ønsker å utvikle programmer og spill. Utvalget jobber for å engasjere studenter til å jobbe på tvers av studieretningene. Det vil bli holdt workshops hvor man kan lære seg forskjellige språk og programmer. Det blir mye moro med sosiale kvelder og spennende prosjekter!  Her er det en unik mulighet for å få nye venner og utvikle seg selv på mange områder.', 'f4721c');